package java220414;

public class main1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//#1. 사칙연산, 연산 후 대입
		int k36_ii; // k36_ii라는 정수형 변수를 선언
		k36_ii = 1 + 2; // 1+2연산을 수행 하기 위해 k36_ii에 집어넣음
		System.out.printf("#1-1 : %d\n", k36_ii); //위 연산을 수행하여 출력함

		k36_ii = 1 + 2 * 3; // 1+2*3연산을 수행 하기 위해 k36_ii에 집어넣음
		System.out.printf("#1-2 : %d\n", k36_ii); //위 연산을 수행하여 출력함
	}

}
